import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import styles from "../CSS/component.module.css";

export const Schema = ({ pair }) => {
  const dataType = [
    "bit",
    "tinyint",
    "smallint",
    "int",
    "bigint",
    "decimal",
    "numeric",
    "float",
    "real",
    "date",
    "time",
    "datetime",
    "timestamp",
    "year",
    "char",
    "varchar",
    "text",
    "nchar",
    "nvachar",
    "ntext",
    "binary",
    "varbinary",
    "image",
    "clob",
    "bclob",
    "xml",
    "json",
  ];
  const maxList = ["varbinary", "nvarchar", "varchar"];
  const [toggleMax, setToggleMax] = useState(false);
  const [maxLength, setMaxLength] = useState(20);

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <div>
        <TextField
          style={{ minWidth: "200px", marginRight: "10px" }}
          id="name"
          label="속성 이름"
          placeholder="속성 이름을 입력하시오."
          value={pair.name}
          onChange={(e) => (pair.name = e.target.value)}
        />
      </div>
      <div>
        <FormControl style={{ minWidth: "200px" }}>
          <InputLabel>속성타입</InputLabel>
          <Select
            id="type"
            value={pair.type}
            onChange={(e) => {
              if (maxList.includes(e.target.value)) {
                setToggleMax(true);
              }
              pair.type = e.target.value;
            }}
          >
            {dataType.map((type) => {
              return <MenuItem value={type}>{type}</MenuItem>;
            })}
          </Select>
        </FormControl>
      </div>
      {toggleMax && (
        <div>
          <TextField
            style={{ minWidth: "200px", marginRight: "10px" }}
            id="name"
            label="Max Length"
            placeholder="최대값을 설정하시오. (기본값=20)"
            value={maxLength}
            onChange={(e) => {
              setMaxLength(e.target.value);
            }}
          />
        </div>
      )}
    </div>
  );
};
