import React, { useState } from "react";
import styles from "../CSS/mainstyle.module.css";
import AddCircleIcon from "@material-ui/icons/AddCircle";
import RemoveCircleIcon from "@material-ui/icons/RemoveCircle";
import IconButton from "@material-ui/core/IconButton";

import { Schema } from "../Components/Schema";

export const CreateTaskTwo = ({ setStep }) => {
  const [num, setNum] = useState(1);
  const [row, setRow] = useState([<Schema key={num} />]);

  const handleAdd = () => {
    setNum(num + 1);
    setRow([...row, <Schema key={num} />]);
    console.log(num);
    console.log(row);
  };

  const handleRemove = () => {};
  return (
    <div className={`${styles.sub_container_a} ${styles.ninety}`}>
      <div className={styles.title}>
        <h1>태스크 데이터 테이블 스키마 설정</h1>
        <form noValidate autoComplete="off">
          <div className={styles.scrollabe_div}>{row}</div>
        </form>
        <div className={styles.button_row}>
          <IconButton onClick={() => handleRemove()}>
            <RemoveCircleIcon />
          </IconButton>
          <IconButton onClick={() => handleAdd()}>
            <AddCircleIcon />
          </IconButton>
        </div>
      </div>
    </div>
  );
};
