import React, { useState, useEffect } from "react";
import styles from "../CSS/loginstyle.module.css";
import { useHistory } from "react-router-dom";
import logo from "../Images/로고.png";
import { User } from "./classes";

export const Login = () => {
  var id, pw;
  var type = {
    Submitter: "제출자",
    Administrator: "관리자",
    Estimator: "평가자",
  };
  var history = useHistory();
  const axios = require("axios").default;
  const [user, setUser] = useState();
  const [data, setData] = useState();
  const [first, setFirst] = useState(true);

  const handleLogin = async () => {
    const url = "/api/loginAuth";
    await axios.get(url + `/${id}&${pw}`).then((res) => {
      setData(res);
      setUser(res.data[0]);
    });
  };

  useEffect(() => {
    if (first) {
      setFirst(false);
      return;
    }
    if (!user) {
      alert("아이디와 비밀번호를 확인해주세요.");
      return;
    }
    if (user.length === 0) {
      alert("아이디와 비밀번호를 확인해주세요.");
      return;
    }
    let loginInfo = new User(
      user.AccountID,
      user.UserID,
      type[user.Role],
      user.Name,
      user.Gender.data,
      user.Address,
      user.BirthDate,
      user.Phone
    );
    console.log(loginInfo);
    localStorage.setItem(`user`, JSON.stringify(loginInfo));
    history.push("/");
  }, [data, user]);

  return (
    <div className={styles.main_container}>
      <div className={styles.div_signin}>
        <img className={styles.main_logo} src={logo} alt="메인 로고" />
        <p className={`${styles.notice} ${styles.login_p}`}>로그인 하세요</p>
        <input
          type="email"
          id="inputEmail"
          className={styles.div_input}
          placeholder="User ID"
          value={id}
          onChange={(e) => (id = e.target.value)}
        />
        <input
          type="password"
          id="inputPassword"
          className={styles.div_input}
          placeholder="Password"
          value={pw}
          onChange={(e) => (pw = e.target.value)}
        />
        <br />
        <button
          className={styles.button}
          type="submit"
          onClick={() => handleLogin()}
        >
          로그인
        </button>
        <br />
        <a
          href=""
          id={styles.div_signup}
          className={styles.login_a}
          href="./signup"
        >
          회원가입
        </a>
        <p className={`${styles.copyright} ${styles.login_p}`}>
          &copy; 연세대학교 데이터베이스 14팀
        </p>
      </div>
    </div>
  );
};
