import React, { useState, useEffect } from "react";
import styles from "../CSS/mainstyle.module.css";

import { UDE } from "../Components/classes";
import { UDS } from "../Components/classes";
import { AdminNav } from "../Components/AdminNav";
import { UserDetailEstimatorNav } from "../Components/UserDetailEstimatorNav";
import { UserDetailEstimator } from "../Components/UserDetailEstimator";
import { useHistory, Link } from "react-router-dom";
import { UserDetailSubmitterNav } from "../Components/UserDetailSubmitterNav";
import { UserDetailSubmitter } from "../Components/UserDetailSubmitter";
import { Loading } from "../Components/Loading";

const UserDetail = (props) => {
  var a = new UDE(123, "태스크1", 100, 10, 0.1, "./", 7);
  var b = new UDS("강현", 100, 10, 1, 0.1, 200, 0.7, true);
  const user = props.location.user;
  const taskID = props.location.taskID;
  const newProps = props.location.newProps;
  const axios = require("axios").default;
  const [udeList, setUdeList] = useState([a, a, a, a, a, a, a, a, a, a, a]);
  const [udsList, setUdsList] = useState([b, b, b, b, b, b, b, b, b, b, b]);
  const [mainData, setMainData] = useState();
  const [loading, setLoading] = useState(true);

  var logInfo;
  var history = useHistory();
  const loggedIn = localStorage.getItem("user");
  if (loggedIn) {
    logInfo = JSON.parse(loggedIn);
  } else {
    history.push("/");
  }

  useEffect(() => {
    const fetchMain = async () => {
      await axios
        .get(`/UserDetail/main/${user.type}/${user.accountID}`)
        .then((res) => {
          setMainData(res.data[0]);
        });
    };
    setLoading(true);
    fetchMain();
  }, []);

  useEffect(() => {
    if (!mainData) return;
    // setLoading(false);
  }, [mainData]);

  return (
    <div>
      <div>
        <AdminNav
          userType={logInfo.userType}
          name={logInfo.name}
          userID={logInfo.userID}
        />
        {loading && <Loading />}
        {!loading && (
          <div className={styles.center_all}>
            <div className={styles.main_container}>
              {user.type === "제출자" && (
                <div className={styles.mc}>
                  <div className={styles.etr}>
                    <div>
                      <h2>이름</h2>
                      <p>{user.name}</p>
                    </div>
                    <div>
                      <h2>참여태스크수</h2>
                      <p>{user.name}</p>
                    </div>
                    <div>
                      <h2>총제출수</h2>
                      <p>{user.name}</p>
                    </div>
                    <div>
                      <h2>점수</h2>
                      <p>{user.name}</p>
                    </div>
                  </div>
                  <div className={styles.ec}>
                    <UserDetailSubmitterNav />
                    <div
                      className={styles.scrollable_div}
                      style={{ height: "300px" }}
                    >
                      {udsList.map((item) => {
                        return (
                          <UserDetailSubmitter top={item.task} uds={item} />
                        );
                      })}
                    </div>
                  </div>
                </div>
              )}
              {user.type === "평가자" && (
                <div className={styles.mc}>
                  <div className={styles.etr}>
                    <div>
                      <h2>이름</h2>
                      <p>{mainData.Name}</p>
                    </div>
                    <div>
                      <h2>총 평가파일 수</h2>
                      <p>{mainData.Total_File}</p>
                    </div>
                    <div>
                      <h2>평가 대기파일 수</h2>
                      <p>{mainData.Total_Queue}</p>
                    </div>
                  </div>
                  <div className={styles.ec}>
                    <UserDetailEstimatorNav />
                    <div
                      className={styles.scrollable_div}
                      style={{ height: "300px" }}
                    >
                      {udeList.map((item) => {
                        return <UserDetailEstimator ude={item} />;
                      })}
                    </div>
                  </div>
                </div>
              )}
              {newProps && (
                <Link
                  to={{
                    pathname: `/TaskView/${taskID}`,
                    task: newProps.location.task,
                  }}
                >
                  <button className={styles.add_btn} style={{ margin: "10px" }}>
                    닫기
                  </button>
                </Link>
              )}
              {!newProps && (
                <Link
                  to={{
                    pathname: "/user",
                  }}
                >
                  <button className={styles.add_btn} style={{ margin: "10px" }}>
                    닫기
                  </button>
                </Link>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDetail;
