import React from "react";
import { useHistory } from "react-router-dom";
import { AdminNav } from "../Components/AdminNav";
import styles from "../CSS/mainstyle.module.css";

const AdminUserMng = () => {
  var logInfo;
  var history = useHistory();
  const loggedIn = localStorage.getItem("user");
  if (loggedIn) {
    logInfo = JSON.parse(loggedIn);
  } else {
    history.push("/");
  }

  return (
    <div>
      <AdminNav
        userType={"관리자"}
        name={logInfo.name}
        userID={logInfo.userID}
      />
      <div className={styles.center_all}>
        <h2 className={styles.list_title}>회원 목록</h2>
        <div className={styles.main_container}>
          <div className={styles.sub_container_1}>
            <div className={styles.scrollable_div}></div>
          </div>
        </div>
        <div className={styles.last_row}>
          <div className={styles.dropdown} style={{ width: "200px" }}>
            <option value="0">검색옵션:</option>
            <option value="1">이름</option>
            <option value="2">역할</option>
            <option value="3">나이</option>
            <option value="4">성별</option>
            <option value="5">ID</option>
            <option value="5">참여 태스크</option>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminUserMng;
